import React, { useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, GeoJSON } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet's default icon path for Create React App
L.Icon.Default.mergeOptions({
  iconRetinaUrl: process.env.PUBLIC_URL + '/marker-icon-2x.png',
  iconUrl: process.env.PUBLIC_URL + '/marker-icon.png',
  shadowUrl: process.env.PUBLIC_URL + '/marker-shadow.png',
});

function App() {
  const [address, setAddress] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const lookup = async () => {
    setError('');
    setResult(null);
    setLoading(true);
    try {
      const res = await axios.post('/lookup', { address });
      setResult(res.data);
    } catch (e) {
      setError(e.response?.data?.error || 'Lookup failed');
    } finally {
      setLoading(false);
    }
  };

  const lat = result?.location?.lat;
  const lon = result?.location?.lon;
  const geometry = result?.district?.geometry;

  return (
    <div style={{ maxWidth: 600, margin: '2rem auto', fontFamily: 'sans-serif' }}>
      <h1 style={{ color: '#b22234', textAlign: 'center', marginBottom: 0 }}>
        <span role="img" aria-label="US Flag">ðŸ‡ºðŸ‡¸</span> USASchoolFinder
      </h1>
      <p style={{ color: '#3c3b6e', textAlign: 'center', marginTop: 0, fontWeight: 'bold' }}>
        Find your official school district anywhere in the USA
      </p>
      <input
        value={address}
        onChange={e => setAddress(e.target.value)}
        placeholder="Enter address"
        style={{ width: '100%', padding: 8, fontSize: 16, border: '2px solid #b22234', borderRadius: 4 }}
      />
      <button onClick={lookup} style={{ marginTop: 10, padding: 8, fontSize: 16, background: '#3c3b6e', color: 'white', border: 'none', borderRadius: 4 }} disabled={loading}>
        {loading ? 'Looking up...' : 'Lookup'}
      </button>
      {error && <div style={{ color: 'red', marginTop: 10 }}>{error}</div>}
      {result && (
        <div style={{ marginTop: 20 }}>
          <b>District:</b> {result.district?.properties?.NAME || 'No district name found'}
          <br />
          <b>Location:</b> {lat}, {lon}
          <div style={{ height: 400, width: '100%', marginTop: 20 }}>
            <MapContainer center={[lat, lon]} zoom={12} style={{ height: '100%', width: '100%' }}>
              <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
              <Marker position={[lat, lon]} />
              {geometry && <GeoJSON data={geometry} style={{ color: 'blue', weight: 2 }} />}
            </MapContainer>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
